import React, { useEffect, useState } from 'react';
import logo from './logo.svg';
import './App.css';
import 'bootstrap/dist/css/bootstrap.min.css';
import axios from 'axios';

const App = () => {
  const [name, setName] = useState(undefined);
  const [email, setEmail] = useState(undefined);
  const [editableId, setEditableId]= useState(undefined);
  const [data, setData] = useState([
    {
      "id": 1,
      "name": "rushi",
      "lastName": "kantaria",
      "email": "email",
      "email": "email",
      "email": "email",
      "save": "save"
    },
    {
      "id": 2,
      "name": "mihir",
      "lastName": "kantaria",
      "email": "email",
      "email": "email",
      "email": "email",
      "save": "save"
    },
    {
      "id": 3,
      "name": "zakir",
      "lastName": "kantaria",
      "email": "email",
      "email": "email",
      "email": "email",
      "save": "save"
    },
    {
      "id": 4,
      "name": "deepkbhai",
      "lastName": "kantaria",
      "email": "email",
      "email": "email",
      "email": "email",
      "save": "save"
    }
  ])
  console.log("name- ------>", name);
  console.log("editableId ------>", editableId);
  const deletitem = (slectedData) => {
    let updatedData = [];
    updatedData = data.map((item) => item.id !== slectedData.id && item);
    setData(updatedData)
  }
  // useEffect(() => {
  //   axios({
  //     method: 'get',
  //     url: 'http://143.110.254.46:8084/api/pending-tournaments',
  //   })
  //   .then(function (response) {
  //     console.log("resp ---->", response.data);
  //   });
  // });
  const handleEmailChange = (event) => {
    setEmail(event.target.value)
  }
  const handleNameChange = (event) => {
    setName(event.target.value)
  }
  return (
    <div className="App">
        {data.map((item) => {
          return(
            <div>
              {item && 
                <div className="data-fatch">
                  <p>{item.name}</p>
                  <p>{item.lastName}</p>
                  <p>{item.email}</p>
                  <p>{item.email}</p>
                  <p><button onClick={() => deletitem(item)}>Delelt</button><button onClick={() => setEditableId(item.id)}>edit</button></p>
                </div>
              }
            </div>
          )
        })}
        <div style={{marginTop: 40}}>
          <div style={{flexDirection: "row"}}>
            <a style={{marginRight: 12}}>Name:</a>
            <input type="text" onChange={handleNameChange} name="title" />
          </div>
          <div style={{flexDirection: "row", marginTop: 15}}>
            <a style={{marginRight: 14}}>Email</a>
            <input type="text" onChange={handleEmailChange} name="title" />
          </div>
          <button style={{marginTop: 15}}>submit</button>
        </div>
    </div>
  );
}

export default App;
